import React from 'react';
import { Link } from 'react-router-dom';
import { useCarrito } from './CarritoContext'; 
import './styles/Carrito.css';

function Carrito() {
  const { cart, setCart } = useCarrito(); 
  const total = cart.reduce((acc, pizza) => acc + pizza.price * (pizza.quantity || 1), 0); 

  const removeFromCart = (index) => {
    const newCart = cart.filter((_, i) => i !== index);
    setCart(newCart);
  };

  const incrementPizza = (index) => {
    setCart((prev) => {
      const newCart = [...prev];
      newCart[index] = { ...newCart[index], quantity: (newCart[index].quantity || 1) + 1 };
      return newCart;
    });
  };

  const decrementPizza = (index) => {
    setCart((prev) => {
      const newCart = [...prev];
      const quantity = newCart[index].quantity || 1;
      if (quantity > 1) {
        newCart[index].quantity = quantity - 1;
      } else {
        removeFromCart(index);
      }
      return newCart;
    });
  };

  return (
    <div className="carrito-container">
      <h2>Carrito de Compras</h2>
      <Link to="/menu" className="back-button">Volver al Men√∫</Link>
      {cart.length === 0 ? (
        <p>No hay productos en el carrito.</p>
      ) : (
        <div>
          {cart.map((pizza, index) => (
            <div key={index} className="cart-item">
              <h3>{pizza.name}</h3>
              <p>Precio: ${pizza.price} COP</p>
              <p>Cantidad: {pizza.quantity || 1}</p>
              <button onClick={() => incrementPizza(index)}>Aumentar</button>
              <button onClick={() => decrementPizza(index)}>Reducir</button>
              <button onClick={() => removeFromCart(index)}>Eliminar</button>
            </div>
          ))}
          <h3>Total: ${total} COP</h3>
          <Link to="/pedido" className="btn">Proceder al Pago</Link>
        </div>
      )}
    </div>
  );
}

export default Carrito;
