import React from 'react';
import { Link } from 'react-router-dom';
import { useCarrito } from './CarritoContext'; 
import './styles/Menu.css';

const pizzasData = [
    { id: 1, name: 'Pizza Hawaiana', price: 20000 },
    { id: 2, name: 'Pizza Peperoni', price: 20000 },
    { id: 3, name: 'Pizza Champiñones', price: 20000 },
];

function Menu() {
    const { cart, setCart } = useCarrito(); 

    const addToCart = (pizza) => {
        
        const existingPizza = cart.find(item => item.id === pizza.id);
        if (existingPizza) {
            
            setCart(cart.map(item => 
                item.id === pizza.id 
                ? { ...existingPizza, quantity: (existingPizza.quantity || 1) + 1 }
                : item
            ));
        } else {
            
            setCart([...cart, { ...pizza, quantity: 1 }]);
        }
    };

    return (
        <div className="menu-container">
            <h2>Nuestro Menú</h2>
            <Link to="/" className="back-button">Volver a Inicio</Link>
            <div className="pizza-list">
                {pizzasData.map((pizza) => (
                    <div key={pizza.id} className="pizza-item">
                        <h3>{pizza.name}</h3>
                        <p>Precio: ${pizza.price} COP</p>
                        <button onClick={() => addToCart(pizza)}>Llevar</button>
                    </div>
                ))}
            </div>
            <Link to="/carrito" className="btn">Ir al Carrito</Link>
        </div>
    );
}

export default Menu;
