import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Pedido.css'; 

const Pedido = () => {
    const [nombre, setNombre] = useState('');
    const [apellido, setApellido] = useState('');
    const [email, setEmail] = useState('');
    const [direccion, setDireccion] = useState('');
    const [metodoPago, setMetodoPago] = useState('');

    const navigate = useNavigate();

    const handlePago = () => {
       
        navigate('/gracias'); 
    };

    return (
        <div className="pedido-container">
            <Link to="/carrito" className="regresar-button">Regresar al Carrito</Link>
            <h1 className="pedido-title">Detalles del Pedido</h1>
            <form className="pedido-form">
                <div className="form-group">
                    <label htmlFor="nombre">Nombre:</label>
                    <input
                        type="text"
                        id="nombre"
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="apellido">Apellido:</label>
                    <input
                        type="text"
                        id="apellido"
                        value={apellido}
                        onChange={(e) => setApellido(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="direccion">Direcci√≥n:</label>
                    <input
                        type="text"
                        id="direccion"
                        value={direccion}
                        onChange={(e) => setDireccion(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="metodoPago">M√©todo de Pago:</label>
                    <select
                        id="metodoPago"
                        value={metodoPago}
                        onChange={(e) => setMetodoPago(e.target.value)}
                        required
                    >
                        <option value="">Seleccione un m√©todo</option>
                        <option value="nequi">Nequi</option>
                        <option value="bancolombia">Bancolombia</option>
                        <option value="efectivo">Efectivo</option>
                    </select>
                </div>
                <button type="button" className="pagar-button" onClick={handlePago}>Pagar</button>
            </form>
        </div>
    );
};

export default Pedido;
