import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './styles/Pedido.css';

function Pedido() {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [email, setEmail] = useState('');
  const [direccion, setDireccion] = useState('');
  const [metodo, setMetodo] = useState('');

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí se podría agregar lógica para manejar el pago
    navigate('/gracias');
  };

  return (
    <div className="pedido-container">
      <h2>Formulario de Pedido</h2>
      <Link to="/menu" className="back-button">Volver al Menú</Link>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
        <input type="text" placeholder="Apellido" value={apellido} onChange={(e) => setApellido(e.target.value)} required />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="text" placeholder="Dirección" value={direccion} onChange={(e) => setDireccion(e.target.value)} required />
        
        <select value={metodo} onChange={(e) => setMetodo(e.target.value)} required>
          <option value="">Método de Pago</option>
          <option value="Nequi">Nequi</option>
          <option value="Bancolombia">Bancolombia</option>
          <option value="Efectivo">Efectivo</option>
        </select>
        <button type="submit">Pagar</button>
      </form>
    </div>
  );
}

export default Pedido;
